import { useEffect, useState } from "react";
import { Box, Button, Typography, Paper, Fade, Chip, Grid, Card, CardContent, Avatar, Divider } from "@mui/material";
import { getProcesses, getQuestions, saveResponses, saveSuggestion, getCareers, getAreas } from "../../services/api";
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import SchoolIcon from '@mui/icons-material/School';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import RestartAltIcon from '@mui/icons-material/RestartAlt';

const ProgressBar = ({ value }) => (
  <Box sx={{ mb: 4 }}>
    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
      <Typography variant="body2" color="#2e7d32" fontWeight="600">Progreso del Test</Typography>
      <Typography variant="body2" color="#2e7d32" fontWeight="600">{Math.round(value)}%</Typography>
    </Box>
    <Box sx={{ height: 12, borderRadius: 6, backgroundColor: '#e8f5e8', overflow: 'hidden' }}>
      <Box sx={{ width: `${value}%`, height: '100%', background: 'linear-gradient(90deg, #4caf50 0%, #2e7d32 100%)', borderRadius: 6, transition: 'width 0.3s' }} />
    </Box>
  </Box>
);

const ResultsView = ({ areaInfo, carreras, onRestart }) => (
  <Box sx={{ 
    minHeight: '100vh', 
    width: '100%',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    py: 3,
    px: 2
  }}>
    <Fade in={true} timeout={800}>
      <Box sx={{ maxWidth: 1000, width: '100%', mx: 'auto' }}>
        {/* Header de Resultados */}
        <Paper 
          elevation={0} 
          sx={{ 
            borderRadius: '20px',
            mb: 3,
            overflow: 'hidden',
            background: 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)',
            color: 'white',
            position: 'relative'
          }}
        >
          <Box sx={{ p: 4, textAlign: 'center', position: 'relative' }}>
            {/* Decoraci√≥n de fondo */}
            <Box sx={{
              position: 'absolute',
              top: -15,
              right: -15,
              width: 150,
              height: 150,
              borderRadius: '50%',
              background: 'rgba(255,255,255,0.1)',
              zIndex: 0
            }} />
            <Box sx={{
              position: 'absolute',
              bottom: -20,
              left: -20,
              width: 100,
              height: 100,
              borderRadius: '50%',
              background: 'rgba(255,255,255,0.08)',
              zIndex: 0
            }} />

            <Avatar sx={{ 
              width: 70,
              height: 70, 
              mx: 'auto', 
              mb: 2,
              bgcolor: 'rgba(255,255,255,0.2)',
              position: 'relative',
              zIndex: 1
            }}>
              <CheckCircleIcon sx={{ fontSize: 40, color: 'white' }} />
            </Avatar>

            <Typography 
              variant="h4"
              fontWeight="bold" 
              gutterBottom
              sx={{ position: 'relative', zIndex: 1 }}
            >
              ¬°Felicitaciones!
            </Typography>

            <Typography 
              variant="body1"
              sx={{ opacity: 0.9, position: 'relative', zIndex: 1 }}
            >
              Has completado exitosamente tu test vocacional
            </Typography>
          </Box>
        </Paper>

        {/* Contenido Principal - Grid con 2 columnas */}
        <Grid container spacing={3} sx={{ mb: 4 }}>
          {/* Perfil Vocacional - Ahora con el mismo ancho */}
          <Grid item xs={12} md={6}>
            <Paper 
              elevation={0} 
              sx={{ 
                borderRadius: '16px',
                p: 3,
                height: '100%',
                border: '2px solid #b9f6ca',
                background: 'linear-gradient(135deg, #ffffff 0%, #f9fff9 100%)',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <Avatar sx={{ 
                  width: 50, 
                  height: 50, 
                  mr: 2, 
                  bgcolor: '#4caf50' 
                }}>
                  <TrendingUpIcon sx={{ fontSize: 24 }} />
                </Avatar>
                <Box>
                  <Typography variant="h6" color="#2e7d32" fontWeight="600">
                    Tu Perfil Vocacional
                  </Typography>
                  <Typography variant="caption" color="#666">
                    Basado en tus respuestas
                  </Typography>
                </Box>
              </Box>

              <Divider sx={{ my: 2, borderColor: '#b9f6ca' }} />

              {/* Contenido centrado del perfil */}
              <Box sx={{ 
                flex: 1,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                textAlign: 'center',
                py: 2
              }}>
                <Box
                  sx={{
                    px: 3,
                    py: 2,
                    backgroundColor: '#e8f5e8',
                    borderRadius: '16px',
                    border: '2px solid #b9f6ca',
                    width: '100%',
                    maxWidth: '280px',
                    mb: 2
                  }}
                >
                  <Typography 
                    variant="h5" 
                    color="#2e7d32" 
                    fontWeight="bold"
                    sx={{ mb: 1 }}
                  >
                    {areaInfo ? areaInfo.nombre : "√Årea no identificada"}
                  </Typography>
                </Box>

                <Typography 
                  variant="body2"
                  color="#666"
                  sx={{ maxWidth: '260px', lineHeight: 1.5 }}
                >
                  {areaInfo 
                    ? "Esta √°rea se alinea perfectamente con tus intereses y aptitudes detectados en el test."
                    : "No pudimos identificar un √°rea espec√≠fica. Te recomendamos realizar el test nuevamente para obtener mejores resultados."
                  }
                </Typography>

                {/* Indicador visual adicional */}
                {areaInfo && (
                  <Box sx={{ mt: 2, display: 'flex', alignItems: 'center' }}>
                    <Box sx={{
                      width: 8,
                      height: 8,
                      borderRadius: '50%',
                      bgcolor: '#4caf50',
                      mr: 1
                    }} />
                    <Typography variant="caption" color="#4caf50" fontWeight="600">
                      Compatibilidad Alta
                    </Typography>
                  </Box>
                )}
              </Box>
            </Paper>
          </Grid>

          {/* Carreras Recomendadas */}
          <Grid item xs={12} md={6}>
            <Paper 
              elevation={0} 
              sx={{ 
                borderRadius: '16px',
                p: 3,
                height: '100%',
                border: '2px solid #b9f6ca',
                background: 'linear-gradient(135deg, #ffffff 0%, #f9fff9 100%)'
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                <Avatar sx={{ 
                  width: 50,
                  height: 50, 
                  mr: 2, 
                  bgcolor: '#4caf50' 
                }}>
                  <SchoolIcon sx={{ fontSize: 24 }} />
                </Avatar>
                <Box>
                  <Typography variant="h6" color="#2e7d32" fontWeight="600">
                    Carreras Recomendadas
                  </Typography>
                  <Typography variant="caption" color="#666">
                    {carreras.length} opciones encontradas
                  </Typography>
                </Box>
              </Box>

              <Divider sx={{ my: 2, borderColor: '#b9f6ca' }} />

              <Box sx={{ 
                maxHeight: 300,
                overflow: 'auto',
                '&::-webkit-scrollbar': {
                  width: 6,
                },
                '&::-webkit-scrollbar-track': {
                  background: '#f1f1f1',
                  borderRadius: 3,
                },
                '&::-webkit-scrollbar-thumb': {
                  background: '#4caf50',
                  borderRadius: 3,
                },
              }}>
                {carreras.length > 0 ? (
                  <Grid container spacing={1.5}>
                    {carreras.map((carrera, index) => (
                      <Grid item xs={12} key={carrera.id}>
                        <Card 
                          elevation={0}
                          sx={{ 
                            border: '1px solid #b9f6ca',
                            borderRadius: '8px',
                            transition: 'all 0.3s ease',
                            '&:hover': {
                              transform: 'translateY(-1px)',
                              boxShadow: '0 3px 15px rgba(76, 175, 80, 0.15)',
                              borderColor: '#4caf50'
                            }
                          }}
                        >
                          <CardContent sx={{ p: 1.5 }}>
                            <Box sx={{ display: 'flex', alignItems: 'center' }}>
                              <Box sx={{
                                width: 32,
                                height: 32,
                                borderRadius: '50%',
                                background: `linear-gradient(45deg, #4caf50, #2e7d32)`,
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                mr: 1.5,
                                color: 'white',
                                fontSize: '14px',
                                fontWeight: 'bold'
                              }}>
                                {index + 1}
                              </Box>
                              <Typography 
                                variant="body1"
                                color="#2e7d32" 
                                fontWeight="600"
                                sx={{ flex: 1, fontSize: '0.95rem' }}
                              >
                                {carrera.nombre}
                              </Typography>
                            </Box>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                ) : (
                  <Box sx={{ textAlign: 'center', py: 4 }}>
                    <Typography variant="body2" color="#666">
                      No se encontraron carreras para esta √°rea
                    </Typography>
                  </Box>
                )}
              </Box>
            </Paper>
          </Grid>
        </Grid>

        {/* Acciones */}
        <Box sx={{ textAlign: 'center', mt: 4 }}>
          <Button
            onClick={onRestart}
            variant="contained"
            size="medium"
            startIcon={<RestartAltIcon />}
            sx={{
              backgroundColor: '#4caf50',
              color: 'white',
              px: 3,
              py: 1.5,
              borderRadius: '12px',
              fontSize: '16px',
              fontWeight: '600',
              textTransform: 'none',
              boxShadow: '0 6px 20px rgba(76, 175, 80, 0.3)',
              '&:hover': {
                backgroundColor: '#2e7d32',
                transform: 'translateY(-1px)',
                boxShadow: '0 8px 24px rgba(76, 175, 80, 0.4)',
              },
              transition: 'all 0.3s ease'
            }}
          >
            Realizar Test Nuevamente
          </Button>

          <Typography 
            variant="caption"
            color="#666" 
            sx={{ mt: 1.5, display: 'block' }}
          >
            ¬øQuieres explorar otras opciones? Puedes repetir el test cuando gustes
          </Typography>
        </Box>
      </Box>
    </Fade>
  </Box>
);
export default function TestFlow() {
  const [proceso, setProceso] = useState(null);
  const [preguntas, setPreguntas] = useState([]);
  const [current, setCurrent] = useState(0);
  const [respuestas, setRespuestas] = useState([]);
  const [finished, setFinished] = useState(false);

  const [areaInfo, setAreaInfo] = useState(null);
  const [carreras, setCarreras] = useState([]);
  const [areasMap, setAreasMap] = useState({});
  const [loading, setLoading] = useState(true);

  const usuario_id = localStorage.getItem("usuario_id");

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
        const procesos = await getProcesses();
        const procesoActivo = procesos[0];
        setProceso(procesoActivo);
        const preguntasBD = await getQuestions(procesoActivo.id);
        setPreguntas(preguntasBD);
      } catch (e) {
        console.error("Error cargando datos:", e);
      }
      setLoading(false);
    }
    fetchData();
  }, []);

  // Calcular sugerencia cuando termine - L√ìGICA CORREGIDA
  // Calcular sugerencia cuando termine - L√ìGICA CORREGIDA
useEffect(() => {
  if (finished && respuestas.length === preguntas.length) {
    const areaCount = {};
    console.log("=== INICIANDO C√ÅLCULO DE √ÅREA SUGERIDA ===");

    respuestas.forEach((respuesta) => {
      const pregunta = preguntas.find(p => Number(p.id) === Number(respuesta.pregunta_id));
      if (!pregunta) {
        console.warn("‚ùå Pregunta no encontrada:", respuesta.pregunta_id);
        return;
      }

      const opcion = pregunta.opciones.find(o => Number(o.id) === Number(respuesta.opcion_id));
      if (!opcion) {
        console.warn("‚ùå Opci√≥n no encontrada:", respuesta.opcion_id);
        return;
      }

      const esInteres =
        opcion.texto.toLowerCase().includes("me interesa") ||
        opcion.interes === 1 ||
        opcion.interes === true;

      if (esInteres) {
        const areaIdNum = Number(opcion.area_id);
        areaCount[areaIdNum] = (areaCount[areaIdNum] || 0) + 1;
        console.log(`‚úÖ √Årea ${areaIdNum} sumada, total: ${areaCount[areaIdNum]}`);
      }
    });

    console.log("üìä Conteo final por √°reas:", areaCount);

    // √Årea con mayor puntuaci√≥n
    const areasSorted = Object.entries(areaCount).sort((a, b) => b[1] - a[1]);
    const areaMayorPuntuacion = areasSorted[0];
    const area_id = areaMayorPuntuacion ? Number(areaMayorPuntuacion[0]) : null;

    // Guardar respuestas en BD
    saveResponses(usuario_id, proceso.id, respuestas);

    if (area_id) {
      console.log("üéØ √Årea sugerida con mayor puntaje:", area_id);
      saveSuggestion(usuario_id, area_id);

      // Traer carreras de esa √°rea
      getCareers(area_id)
        .then(carrerasData => {
          console.log("üìö Carreras obtenidas:", carrerasData);
          setCarreras(carrerasData);

          // Traer informaci√≥n del √°rea directamente desde la BD
          getAreas()
            .then(areasData => {
              console.log("üåê √Åreas obtenidas desde BD:", areasData);

              const areaFromDB = areasData.find(a => Number(a.id) === area_id);

              if (areaFromDB) {
                setAreaInfo(areaFromDB);
                console.log("‚úÖ √Årea encontrada en BD:", areaFromDB);
              } else {
                setAreaInfo({ id: area_id, nombre: `√Årea ${area_id}` });
                console.warn("‚ö†Ô∏è √Årea no encontrada en BD, usando fallback");
              }
            })
            .catch(error => {
              console.error("‚ùå Error obteniendo √°reas:", error);
              setAreaInfo({ id: area_id, nombre: `√Årea ${area_id}` });
            });
        })
        .catch(error => {
          console.error("‚ùå Error obteniendo carreras:", error);
          setCarreras([]);
        });
    } else {
      console.log("‚ö†Ô∏è No se pudo determinar un √°rea sugerida");
      setCarreras([]);
      setAreaInfo(null);
    }

    console.log("=== FIN C√ÅLCULO DE √ÅREA SUGERIDA ===");
  }
}, [finished, respuestas, preguntas, usuario_id, proceso]);


  const handleRestart = () => {
    setCurrent(0);
    setRespuestas([]);
    setFinished(false);
    setAreaInfo(null);
    setCarreras([]);
  };

  if (loading) {
    return (
      <Box sx={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center'
      }}>
        <Paper elevation={0} sx={{ p: 4, borderRadius: '16px', textAlign: 'center' }}>
          <Typography variant="h5" color="#2e7d32" fontWeight="600">
            Cargando test...
          </Typography>
        </Paper>
      </Box>
    );
  }

  // Mostrar resultados si el test ha terminado
  if (finished) {
    return (
      <ResultsView 
        areaInfo={areaInfo}
        carreras={carreras}
        onRestart={handleRestart}
      />
    );
  }

  const preguntaActual = preguntas[current];
  if (!preguntaActual) {
    return (
      <Box sx={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center'
      }}>
        <Paper elevation={0} sx={{ p: 4, borderRadius: '16px', textAlign: 'center' }}>
          <Typography variant="h5" color="#2e7d32" fontWeight="600">
            Cargando test...
          </Typography>
        </Paper>
      </Box>
    );
  }

  const handleRespuesta = (opcion_id) => {
    setRespuestas(prev => [...prev, { pregunta_id: preguntaActual.id, opcion_id }]);
    if (current < preguntas.length - 1) {
      setCurrent(current + 1);
    } else {
      setFinished(true);
    }
  };

  const progress = ((current + 1) / preguntas.length) * 100;

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      width: '100%', 
      display: 'flex', 
      flexDirection: 'column',
      alignItems: 'center', 
      justifyContent: 'center',
      py: 4,
      px: 2
    }}>
      <Fade in={true} timeout={600} key={current}>
        <Box sx={{ width: '100%', maxWidth: 800, mx: 'auto' }}>
          {/* Barra de progreso mejorada */}
          <Box sx={{ mb: 4 }}>
            <ProgressBar value={progress} />
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Typography variant="body1" color="#2e7d32" fontWeight="600">
                Pregunta {current + 1} de {preguntas.length}
              </Typography>
              <Chip 
                label={`${Math.round(progress)}% Completado`} 
                color="success" 
                variant="outlined" 
                sx={{ fontWeight: 600 }}
              />
            </Box>
          </Box>

          <Paper 
            elevation={0} 
            sx={{ 
              borderRadius: '32px', 
              overflow: 'hidden',
              border: '3px solid #b9f6ca',
              boxShadow: '0 16px 48px rgba(76, 175, 80, 0.15)'
            }}
          >
            {/* Pregunta mejorada */}
            <Box sx={{ 
              borderBottom: '3px solid #b9f6ca', 
              p: 4, 
              textAlign: 'center',
              background: 'linear-gradient(135deg, #ffffff 0%, #f9fff9 100%)'
            }}>
              <Typography 
                variant="h4" 
                color="#2e7d32" 
                fontWeight="bold"
                sx={{ lineHeight: 1.3 }}
              >
                {preguntaActual.texto}
              </Typography>
            </Box>

            {/* Opciones mejoradas */}
            <Box sx={{ 
              display: 'flex', 
              flexDirection: { xs: 'column', sm: 'row' }, 
              height: { xs: 'auto', sm: 250 } 
            }}>
              {preguntaActual.opciones.map((op, idx) => (
                <Box
                  key={op.id}
                  sx={{
                    flex: 1,
                    borderRight: idx === 0 ? { sm: '3px solid #b9f6ca', xs: 'none' } : 'none',
                    borderBottom: idx === 0 ? { xs: '3px solid #b9f6ca', sm: 'none' } : 'none',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    position: 'relative',
                    overflow: 'hidden'
                  }}
                >
                  <Button
                    onClick={() => handleRespuesta(op.id)}
                    variant="contained"
                    fullWidth
                    sx={{
                      height: { xs: 120, sm: 250 },
                      borderRadius: 0,
                      background: 'linear-gradient(135deg, #90EE90 0%, #7dd87d 100%)',
                      color: "#2e7d32",
                      fontSize: { xs: 28, sm: 32 },
                      fontWeight: 600,
                      textTransform: 'none',
                      position: 'relative',
                      overflow: 'hidden',
                      transition: 'all 0.3s ease',
                      "&:hover": { 
                        background: 'linear-gradient(135deg, #77d677 0%, #66cc66 100%)',
                        transform: 'scale(1.02)',
                        boxShadow: '0 8px 24px rgba(76, 175, 80, 0.3)'
                      },
                      "&:before": {
                        content: '""',
                        position: 'absolute',
                        top: 0,
                        left: '-100%',
                        width: '100%',
                        height: '100%',
                        background: 'linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent)',
                        transition: 'left 0.5s ease',
                      },
                      "&:hover:before": {
                        left: '100%'
                      }
                    }}
                  >
                    {op.texto}
                  </Button>
                </Box>
              ))}
            </Box>
          </Paper>
        </Box>
      </Fade>
    </Box>
  );
}
