import { useEffect, useState } from "react";
import { Box, Button, Typography, Paper, Fade, Chip, Dialog, DialogTitle, DialogContent, DialogActions } from "@mui/material";
import { getProcesses, getQuestions, saveResponses, saveSuggestion, getCareers } from "../../services/api";

const ProgressBar = ({ value }) => (
  <Box sx={{ mb: 4 }}>
    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
      <Typography variant="body2" color="#2e7d32" fontWeight="600">Progreso del Test</Typography>
      <Typography variant="body2" color="#2e7d32" fontWeight="600">{Math.round(value)}%</Typography>
    </Box>
    <Box sx={{ height: 12, borderRadius: 6, backgroundColor: '#e8f5e8', overflow: 'hidden' }}>
      <Box sx={{ width: `${value}%`, height: '100%', background: 'linear-gradient(90deg, #4caf50 0%, #2e7d32 100%)', borderRadius: 6, transition: 'width 0.3s' }} />
    </Box>
  </Box>
);

export default function TestFlow() {
  const [proceso, setProceso] = useState(null);
  const [preguntas, setPreguntas] = useState([]);
  const [current, setCurrent] = useState(0);
  const [respuestas, setRespuestas] = useState([]);
  const [finished, setFinished] = useState(false);
  const [areaSugerida, setAreaSugerida] = useState(null);
  const [carreras, setCarreras] = useState([]);
  const [loading, setLoading] = useState(true);
  const [openModal, setOpenModal] = useState(false);

  const usuario_id = localStorage.getItem("usuario_id");

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
        const procesos = await getProcesses();
        const procesoActivo = procesos[0];
        setProceso(procesoActivo);
        const preguntasBD = await getQuestions(procesoActivo.id);
        setPreguntas(preguntasBD);
      } catch (e) {
        console.error("Error cargando datos:", e);
      }
      setLoading(false);
    }
    fetchData();
  }, []);

  // Calcular sugerencia cuando termine
  useEffect(() => {
    if (finished && respuestas.length === preguntas.length) {
      const areaCount = {};

      respuestas.forEach(r => {
        const pregunta = preguntas.find(p => p.id === r.pregunta_id);
        if (!pregunta) return;
        const opcion = pregunta.opciones.find(o => o.id === r.opcion_id);

        // Contar solo "Me interesa" (interes === 1 o true)
        if (opcion && (opcion.interes === 1 || opcion.interes === true)) {
          areaCount[opcion.area_id] = (areaCount[opcion.area_id] || 0) + 1;
          console.log("Se sumó interés en área:", opcion.area_id, "Total:", areaCount[opcion.area_id]);
        }
      });

      const area_id = Object.entries(areaCount).sort((a, b) => b[1] - a[1])[0]?.[0] ?? null;
      setAreaSugerida(area_id);

      saveResponses(usuario_id, proceso.id, respuestas);
      if (area_id) {
        saveSuggestion(usuario_id, area_id);
        getCareers(area_id).then(setCarreras);
        setOpenModal(true); // abrir modal
      }
    }
  }, [finished, respuestas, preguntas, usuario_id, proceso]);

  if (loading) {
    return (
      <Box sx={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <Typography>Cargando test...</Typography>
      </Box>
    );
  }

  const preguntaActual = preguntas[current];
  if (!preguntaActual) {
    return (
      <Box sx={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <Typography>Cargando test...</Typography>
      </Box>
    );
  }

  const handleRespuesta = (opcion_id) => {
    setRespuestas(prev => [...prev, { pregunta_id: preguntaActual.id, opcion_id }]);
    if (current < preguntas.length - 1) {
      setCurrent(current + 1);
    } else {
      setFinished(true);
    }
  };

  return (
    <Box sx={{ minHeight: '100vh', width: '100vw', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <Fade in={true} timeout={600} key={current}>
        <Box sx={{ width: '100%', maxWidth: 700 }}>
          <Paper elevation={0} sx={{ borderRadius: '32px', overflow: 'hidden' }}>
            {/* Pregunta */}
            <Box sx={{ borderBottom: '2px solid #b9f6ca', p: 3, textAlign: 'center' }}>
              <Typography variant="h4" color="#2e7d32" fontWeight="bold">
                {preguntaActual.texto}
              </Typography>
            </Box>
            {/* Opciones */}
            <Box sx={{ display: 'flex', flexDirection: { xs: 'column', sm: 'row' }, height: { xs: 'auto', sm: 220 } }}>
              {preguntaActual.opciones.map((op, idx) => (
                <Box
                  key={op.id}
                  sx={{
                    flex: 1,
                    borderRight: idx === 0 ? { sm: '2px solid #b9f6ca', xs: 'none' } : 'none',
                    borderBottom: idx === 0 ? { xs: '2px solid #b9f6ca', sm: 'none' } : 'none',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center'
                  }}
                >
                  <Button
                    onClick={() => handleRespuesta(op.id)}
                    variant="contained"
                    fullWidth
                    sx={{
                      height: { xs: 100, sm: 220 },
                      borderRadius: 0,
                      backgroundColor: "#90EE90",
                      color: "#2e7d32",
                      fontSize: 32,
                      fontWeight: 500,
                      ":hover": { backgroundColor: "#77d677" }
                    }}
                  >
                    {op.texto}
                  </Button>
                </Box>
              ))}
            </Box>
          </Paper>
        </Box>
      </Fade>

      {/* Modal de sugerencia */}
      <Dialog open={openModal} onClose={() => setOpenModal(false)} fullWidth maxWidth="md">
        <DialogTitle>¡Felicitaciones!</DialogTitle>
        <DialogContent>
          <Typography variant="h5" color="#2e7d32" fontWeight="600">
            Tu perfil vocacional es:
          </Typography>
          <Typography variant="h4" color="#2e7d32" fontWeight="bold" gutterBottom>
            {carreras.length > 0 ? carreras[0].nombre : "Área sugerida"}
          </Typography>
          <Typography variant="h6" sx={{ mt: 2, mb: 1 }}>Carreras recomendadas:</Typography>
          <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
            {carreras.map(c => (
              <Chip key={c.id} label={c.nombre} color="success" variant="outlined" sx={{ fontSize: 16 }} />
            ))}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => window.location.reload()} variant="contained" color="success">
            Realizar Test Nuevamente
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
