  }
};

export default function TestFlow() {
  const [current, setCurrent] = useState(0);
  const [scores, setScores] = useState({
    Ingenier√≠a: 0,
    Artes: 0,
    "Ciencias Sociales": 0,
    Salud: 0
  });
  const [finished, setFinished] = useState(false);
  const [result, setResult] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);

  const handleAnswer = (area, optionIndex) => {
    setSelectedOption(optionIndex);

    setTimeout(() => {
      setScores((prev) => ({
        ...prev,
        [area]: prev[area] + 1
      }));

      if (current < questions.length - 1) {
        setCurrent(current + 1);
        setSelectedOption(null);
      } else {
        // calcular el resultado final
        const newScores = {
          ...scores,
          [area]: scores[area] + 1
        };

        const maxArea = Object.entries(newScores).reduce(
          (a, b) => (a[1] > b[1] ? a : b)
        );
        setResult(maxArea[0]);
        setFinished(true);
      }
    }, 500);
  };

  if (finished) {
    const resultData = areaDescriptions[result];
    return (
      <Box
        sx={{
          minHeight: '100vh',
          width: '100vw',
          background: 'linear-gradient(135deg, #a5d6a7 0%, #43a047 100%)', // Fondo completamente verde
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}
      >
        <Fade in={true} timeout={800}>
          <Paper
            elevation={0}
            sx={{
              p: 6,
              borderRadius: '32px',
              textAlign: "center",
              background: 'linear-gradient(135deg, #ffffff 0%, #f8fffe 100%)',
              boxShadow: '0 20px 60px rgba(46, 125, 50, 0.1)',
              border: '1px solid rgba(76, 175, 80, 0.1)',
              maxWidth: 700,
              width: '100%'
            }}
          >
            {/* Icono y celebraci√≥n */}
            <Box sx={{ mb: 4 }}>
              <Box
                sx={{
                  width: 120,
                  height: 120,
                  borderRadius: '50%',
                  background: 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  margin: '0 auto',
                  mb: 3,
                  boxShadow: '0 10px 30px rgba(76, 175, 80, 0.3)'
                }}
              >
                {resultData.icon}
              </Box>
              <Typography 
                variant="h3" 
