};

// Preguntas alineadas a las facultades/carreras de la universidad
const questions = [
  {
    question: "¬øQu√© actividad disfrutas m√°s?",
    options: [
      { text: "Dise√±ar planos o espacios urbanos", area: "Arquitectura y Urbanismo", icon: "üèõÔ∏è" },
      { text: "Dirigir un negocio o proyecto de marketing", area: "Ciencias Econ√≥micas y Empresariales", icon: "üíº" },
      { text: "Investigar sobre organismos vivos", area: "Ciencias Biol√≥gicas", icon: "üß¨" },
      { text: "Crear soluciones tecnol√≥gicas o construir dispositivos", area: "Ingenier√≠a", icon: "ü§ñ" }
