import { useState } from "react";
import { Box, Card, Typography, Button, Container, LinearProgress, Paper, Fade } from "@mui/material";
import {
  Architecture,
  AccountBalance,
  Science,
  Engineering,
  Gavel,
  Psychology as PsychologyIcon,
  Language,
  LocalHospital,
  MonetizationOn
} from "@mui/icons-material";

// Componente de ProgressBar mejorado
const ProgressBar = ({ value }) => (
  <Box sx={{ mb: 4 }}>
    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
      <Typography variant="body2" color="#2e7d32" fontWeight="600">
        Progreso del Test
      </Typography>
      <Typography variant="body2" color="#2e7d32" fontWeight="600">
        {Math.round(value)}%
      </Typography>
    </Box>
    <LinearProgress
      variant="determinate"
      value={value}
      sx={{
        height: 12,
        borderRadius: 6,
        backgroundColor: '#e8f5e8',
        '& .MuiLinearProgress-bar': {
          background: 'linear-gradient(90deg, #4caf50 0%, #2e7d32 100%)',
          borderRadius: 6,
        },
      }}
    />
  </Box>
);

// Facultades/Áreas y carreras de la universidad
const areaDescriptions = {
  "Arquitectura y Urbanismo": {
    description: "Te motiva crear, diseñar y transformar espacios urbanos y arquitectónicos.",
    careers: [{ name: "Arquitectura", icon: <Architecture sx={{ fontSize: 32, color: '#2e7d32' }} /> }],
    icon: <Architecture sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Ciencias Económicas y Empresariales": {
    description: "Te interesa el mundo de los negocios, la economía y la gestión financiera.",
    careers: [
      { name: "Economía", icon: <MonetizationOn sx={{ fontSize: 28, color: '#2e7d32' }} /> },
      { name: "Administración", icon: <AccountBalance sx={{ fontSize: 28, color: '#2e7d32' }} /> },
      { name: "Contabilidad y Finanzas", icon: <AccountBalance sx={{ fontSize: 28, color: '#2e7d32' }} /> },
      { name: "Marketing Global y Administración Comercial", icon: <MonetizationOn sx={{ fontSize: 28, color: '#2e7d32' }} /> }
    ],
    icon: <AccountBalance sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Ciencias Biológicas": {
    description: "Te interesa el estudio de los seres vivos, la biología y el trabajo científico.",
    careers: [
      { name: "Biología", icon: <Science sx={{ fontSize: 28, color: '#2e7d32' }} /> }
    ],
    icon: <Science sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Ingeniería": {
    description: "Te apasionan las ciencias exactas, el diseño y la resolución de problemas técnicos.",
    careers: [
      { name: "Ingeniería Civil", icon: <Engineering sx={{ fontSize: 28, color: '#2e7d32' }} /> },
      { name: "Ingeniería Electrónica", icon: <Engineering sx={{ fontSize: 28, color: '#2e7d32' }} /> },
      { name: "Ingeniería Industrial", icon: <Engineering sx={{ fontSize: 28, color: '#2e7d32' }} /> },
      { name: "Ingeniería Informática", icon: <Engineering sx={{ fontSize: 28, color: '#2e7d32' }} /> },
      { name: "Ingeniería Mecatrónica", icon: <Engineering sx={{ fontSize: 28, color: '#2e7d32' }} /> }
    ],
    icon: <Engineering sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Humanidades y Lenguas Modernas": {
    description: "Tienes facilidad para los idiomas, la comunicación y la comprensión de culturas.",
    careers: [
      { name: "Traducción e Interpretación", icon: <Language sx={{ fontSize: 28, color: '#2e7d32' }} /> }
    ],
    icon: <Language sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Medicina Humana": {
    description: "Tienes vocación de servicio, interés por la salud y ayudar a los demás.",
    careers: [
      { name: "Medicina Humana", icon: <LocalHospital sx={{ fontSize: 28, color: '#2e7d32' }} /> }
    ],
    icon: <LocalHospital sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Derecho y Ciencia Política": {
    description: "Te motiva la justicia, las leyes y el análisis de los sistemas políticos.",
    careers: [
      { name: "Derecho y Ciencia Política", icon: <Gavel sx={{ fontSize: 28, color: '#2e7d32' }} /> }
    ],
    icon: <Gavel sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Psicología": {
    description: "Te interesa el comportamiento humano y ayudar a las personas a mejorar su bienestar.",
    careers: [
      { name: "Psicología", icon: <PsychologyIcon sx={{ fontSize: 28, color: '#2e7d32' }} /> }
    ],
    icon: <PsychologyIcon sx={{ fontSize: 48, color: '#2e7d32' }} />
  }
};

// Preguntas alineadas a las facultades/carreras de la universidad
const questions = [
  {
    question: "¿Qué actividad disfrutas más?",
    options: [
      { text: "Diseñar planos o espacios urbanos", area: "Arquitectura y Urbanismo", icon: "🏛️" },
      { text: "Dirigir un negocio o proyecto de marketing", area: "Ciencias Económicas y Empresariales", icon: "💼" },
      { text: "Investigar sobre organismos vivos", area: "Ciencias Biológicas", icon: "🧬" },
      { text: "Crear soluciones tecnológicas o construir dispositivos", area: "Ingeniería", icon: "🤖" }
    ]
  },
  {
    question: "¿En qué tipo de materia destacas más?",
    options: [
      { text: "Dibujo técnico o arte", area: "Arquitectura y Urbanismo", icon: "📐" },
      { text: "Matemáticas financieras o economía", area: "Ciencias Económicas y Empresariales", icon: "📊" },
      { text: "Biología o ciencias naturales", area: "Ciencias Biológicas", icon: "🌱" },
      { text: "Física, matemáticas aplicadas o informática", area: "Ingeniería", icon: "⚙️" }
    ]
  },
  {
    question: "¿Qué rol prefieres en un grupo?",
    options: [
      { text: "Imaginar y diseñar edificios o ciudades", area: "Arquitectura y Urbanismo", icon: "🏙️" },
      { text: "Liderar estrategias empresariales", area: "Ciencias Económicas y Empresariales", icon: "📈" },
      { text: "Analizar muestras biológicas", area: "Ciencias Biológicas", icon: "🔬" },
      { text: "Resolver problemas técnicos o de software", area: "Ingeniería", icon: "🖥️" }
    ]
  },
  {
    question: "¿Qué te motiva más en tu futuro profesional?",
    options: [
      { text: "Transformar espacios y paisajes", area: "Arquitectura y Urbanismo", icon: "🏗️" },
      { text: "Crear impacto económico o empresarial", area: "Ciencias Económicas y Empresariales", icon: "💸" },
      { text: "Contribuir a la ciencia mediante la biología", area: "Ciencias Biológicas", icon: "🧪" },
      { text: "Innovar con tecnología y procesos", area: "Ingeniería", icon: "🤖" }
    ]
  },
  {
    question: "¿Qué tipo de libros/artículos disfrutas más?",
    options: [
      { text: "Arquitectura, arte y diseño urbano", area: "Arquitectura y Urbanismo", icon: "🏛️" },
      { text: "Economía, finanzas y empresas", area: "Ciencias Económicas y Empresariales", icon: "📈" },
      { text: "Ciencia, biología y medio ambiente", area: "Ciencias Biológicas", icon: "🌿" },
      { text: "Tecnología, innovación y robótica", area: "Ingeniería", icon: "🤖" }
    ]
  },
  {
    question: "¿Qué área te llama más la atención?",
    options: [
      { text: "Traducción, idiomas y culturas", area: "Humanidades y Lenguas Modernas", icon: "🌍" },
      { text: "Medicina y ciencias de la salud", area: "Medicina Humana", icon: "🩺" },
      { text: "Derecho, leyes y política", area: "Derecho y Ciencia Política", icon: "⚖️" },
      { text: "Comportamiento y mente humana", area: "Psicología", icon: "🧠" }
    ]
  },
  {
    question: "¿Con qué actividad te identificas más?",
    options: [
      { text: "Interpretar textos en varios idiomas", area: "Humanidades y Lenguas Modernas", icon: "🗣️" },
      { text: "Atender pacientes o investigar sobre enfermedades", area: "Medicina Humana", icon: "🏥" },
      { text: "Analizar casos jurídicos o debatir sobre leyes", area: "Derecho y Ciencia Política", icon: "📜" },
      { text: "Ayudar a las personas a resolver conflictos personales", area: "Psicología", icon: "💬" }
    ]
  },
  {
    question: "¿Qué prefieres hacer en tu tiempo libre?",
    options: [
      { text: "Aprender nuevos idiomas", area: "Humanidades y Lenguas Modernas", icon: "📚" },
      { text: "Leer sobre anatomía y salud", area: "Medicina Humana", icon: "🩻" },
      { text: "Participar en debates o actividades cívicas", area: "Derecho y Ciencia Política", icon: "🏛️" },
      { text: "Escuchar y dar consejos a amigos", area: "Psicología", icon: "🗨️" }
    ]
  }
];

const initialScores = Object.keys(areaDescriptions).reduce((acc, area) => {
  acc[area] = 0;
  return acc;
}, {});

export default function TestFlow() {
  const [current, setCurrent] = useState(0);
  const [scores, setScores] = useState(initialScores);
  const [finished, setFinished] = useState(false);
  const [result, setResult] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);

  const handleAnswer = (area, optionIndex) => {
    setSelectedOption(optionIndex);
    setTimeout(() => {
      setScores((prev) => ({
        ...prev,
        [area]: prev[area] + 1
      }));

      if (current < questions.length - 1) {
        setCurrent(current + 1);
        setSelectedOption(null);
      } else {
        const newScores = {
          ...scores,
          [area]: scores[area] + 1
        };
        const maxArea = Object.entries(newScores).reduce(
          (a, b) => (a[1] > b[1] ? a : b)
        );
        setResult(maxArea[0]);
        setFinished(true);
      }
    }, 500);
  };

  if (finished) {
    const resultData = areaDescriptions[result];
    return (
      <Box
        sx={{
          minHeight: '100vh',
          width: '100vw',
          background: 'linear-gradient(135deg, #e8f5e9 0%, #b9f6ca 100%)',          
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}
      >
        <Fade in={true} timeout={800}>
          <Paper
            elevation={0}
            sx={{
              p: 6,
              borderRadius: '32px',
              textAlign: "center",
              background: 'linear-gradient(135deg, #ffffff 0%, #f8fffe 100%)',
              boxShadow: '0 20px 60px rgba(46, 125, 50, 0.1)',
              border: '1px solid rgba(76, 175, 80, 0.1)',
              maxWidth: 700,
              width: '100%'
            }}
          >
            <Box sx={{ mb: 4 }}>
              <Box
                sx={{
                  width: 120,
                  height: 120,
                  borderRadius: '50%',
                  background: 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  margin: '0 auto',
                  mb: 3,
                  boxShadow: '0 10px 30px rgba(76, 175, 80, 0.3)'
                }}
              >
                {resultData.icon}
              </Box>
              <Typography 
                variant="h3" 
                sx={{ 
                  fontWeight: 'bold',
                  background: 'linear-gradient(135deg, #2e7d32 0%, #4caf50 100%)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  mb: 2
                }}
              >
                🎉 ¡Felicitaciones!
              </Typography>
            </Box>

            <Typography variant="h4" color="#1b5e20" fontWeight="bold" gutterBottom>
              Tu perfil vocacional es:
            </Typography>

            <Typography 
              variant="h2" 
              sx={{ 
                fontWeight: "bold", 
                background: 'linear-gradient(135deg, #2e7d32 0%, #4caf50 100%)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                mb: 4
              }}
            >
              {result}
            </Typography>

            <Paper
              sx={{
                p: 4,
                mb: 4,
                backgroundColor: '#f1f8e9',
                border: '2px solid #c8e6c9',
                borderRadius: '20px'
              }}
            >
              <Typography variant="h6" color="#2e7d32" sx={{ mb: 2 }}>
                Descripción de tu perfil:
              </Typography>
              <Typography variant="body1" color="#1b5e20" sx={{ lineHeight: 1.8 }}>
                {resultData.description}
              </Typography>
            </Paper>

            <Paper
              sx={{
                p: 4,
                backgroundColor: '#e8f5e8',
                border: '2px solid #a5d6a7',
                borderRadius: '20px',
                mb: 4
              }}
            >
              <Typography variant="h6" color="#2e7d32" sx={{ mb: 3 }}>
                Carreras recomendadas para ti:
              </Typography>
              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, justifyContent: 'center' }}>
                {resultData.careers.map((career, index) => (
                  <Paper
                    key={index}
                    sx={{
                      px: 3,
                      py: 1.5,
                      backgroundColor: 'white',
                      border: '1px solid #4caf50',
                      borderRadius: '25px',
                      boxShadow: '0 4px 12px rgba(76, 175, 80, 0.2)',
                      display: 'flex',
                      alignItems: 'center',
                      gap: 1.2
                    }}
                  >
                    {career.icon}
                    <Typography variant="body2" color="#2e7d32" fontWeight="600">
                      {career.name}
                    </Typography>
                  </Paper>
                ))}
              </Box>
            </Paper>

            <Button
              variant="contained"
              size="large"
              onClick={() => window.location.reload()}
              sx={{
                background: 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)',
                color: 'white',
                fontWeight: 'bold',
                py: 2,
                px: 4,
                fontSize: '1.1rem',
                borderRadius: '16px',
                textTransform: 'none',
                boxShadow: '0 8px 24px rgba(76, 175, 80, 0.4)',
                '&:hover': {
                  background: 'linear-gradient(135deg, #388e3c 0%, #1b5e20 100%)',
                  boxShadow: '0 12px 32px rgba(76, 175, 80, 0.6)',
                  transform: 'translateY(-2px)',
                },
                transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)'
              }}
            >
              Realizar Test Nuevamente
            </Button>
          </Paper>
        </Fade>
      </Box>
    );
  }

  const data = questions[current];

  return (
    <Box
      sx={{
        minHeight: '100vh',
        width: '100vw',
        background: 'linear-gradient(135deg, #e8f5e9 0%, #b9f6ca 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}
    >
      <Fade in={true} timeout={600} key={current}>
        <Box sx={{ width: '100%', maxWidth: 700 }}>
          <ProgressBar value={((current + 1) / questions.length) * 100} />
          <Paper
            elevation={0}
            sx={{
              p: 5,
              borderRadius: '32px',
              background: 'linear-gradient(135deg, #ffffff 0%, #f8fffe 100%)',
              boxShadow: '0 20px 60px rgba(46, 125, 50, 0.1)',
              border: '1px solid rgba(76, 175, 80, 0.1)'
            }}
          >
            <Box sx={{ textAlign: 'center', mb: 4 }}>
              <Typography 
                variant="body1" 
                color="#4caf50" 
                fontWeight="600" 
                sx={{ mb: 1 }}
              >
                Pregunta {current + 1} de {questions.length}
              </Typography>
              <Typography 
                variant="h4" 
                color="#1b5e20"
                fontWeight="bold"
                sx={{ lineHeight: 1.4 }}
              >
                {data.question}
              </Typography>
            </Box>

            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
              {data.options.map((option, i) => (
                <Button
                  key={i}
                  variant={selectedOption === i ? "contained" : "outlined"}
                  fullWidth
                  size="large"
                  onClick={() => handleAnswer(option.area, i)}
                  disabled={selectedOption !== null}
                  sx={{
                    py: 3,
                    px: 4,
                    fontSize: '1.1rem',
                    fontWeight: '600',
                    borderRadius: '20px',
                    textAlign: 'left',
                    justifyContent: 'flex-start',
                    textTransform: 'none',
                    border: selectedOption === i ? 'none' : '2px solid #c8e6c9',
                    backgroundColor: selectedOption === i 
                      ? 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)' 
                      : 'white',
                    color: selectedOption === i ? 'white' : '#2e7d32',
                    boxShadow: selectedOption === i 
                      ? '0 8px 24px rgba(76, 175, 80, 0.4)'
                      : '0 4px 12px rgba(76, 175, 80, 0.1)',
                    '&:hover': {
                      backgroundColor: selectedOption === i 
                        ? 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)'
                        : '#f1f8e9',
                      borderColor: '#4caf50',
                      transform: 'translateY(-2px)',
                      boxShadow: '0 8px 24px rgba(76, 175, 80, 0.2)',
                    },
                    '&:disabled': {
                      opacity: selectedOption === i ? 1 : 0.6,
                    },
                    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                    display: 'flex',
                    alignItems: 'center',
                    gap: 2
                  }}
                >
                  <Typography sx={{ fontSize: '1.5rem', mr: 1 }}>
                    {option.icon}
                  </Typography>
                  {option.text}
                </Button>
              ))}
            </Box>
          </Paper>
        </Box>
      </Fade>
    </Box>
  );
}
