import { useEffect, useState } from "react";
import { Box, Button, Typography, Paper, Fade, Chip, Grid, Card, CardContent, Avatar, Divider } from "@mui/material";
import { getProcesses, getQuestions, saveResponses, saveSuggestion, getCareers } from "../../services/api";
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import SchoolIcon from '@mui/icons-material/School';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import RestartAltIcon from '@mui/icons-material/RestartAlt';

const ProgressBar = ({ value }) => (
  <Box sx={{ mb: 4 }}>
    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
      <Typography variant="body2" color="#2e7d32" fontWeight="600">Progreso del Test</Typography>
      <Typography variant="body2" color="#2e7d32" fontWeight="600">{Math.round(value)}%</Typography>
    </Box>
    <Box sx={{ height: 12, borderRadius: 6, backgroundColor: '#e8f5e8', overflow: 'hidden' }}>
      <Box sx={{ width: `${value}%`, height: '100%', background: 'linear-gradient(90deg, #4caf50 0%, #2e7d32 100%)', borderRadius: 6, transition: 'width 0.3s' }} />
    </Box>
  </Box>
);

const ResultsView = ({ areaSugerida, carreras, onRestart }) => (
  <Fade in={true} timeout={800}>
    <Box sx={{ 
      minHeight: '100vh', 
      width: '100%',
      py: 6,
      px: 2
    }}>
      <Box sx={{ maxWidth: 1200, mx: 'auto' }}>
        {/* Header de Resultados */}
        <Paper 
          elevation={0} 
          sx={{ 
            borderRadius: '24px', 
            mb: 4, 
            overflow: 'hidden',
            background: 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)',
            color: 'white',
            position: 'relative'
          }}
        >
          <Box sx={{ p: 6, textAlign: 'center', position: 'relative' }}>
            {/* Decoración de fondo */}
            <Box sx={{
              position: 'absolute',
              top: -20,
              right: -20,
              width: 200,
              height: 200,
              borderRadius: '50%',
              background: 'rgba(255,255,255,0.1)',
              zIndex: 0
            }} />
            <Box sx={{
              position: 'absolute',
              bottom: -30,
              left: -30,
              width: 150,
              height: 150,
              borderRadius: '50%',
              background: 'rgba(255,255,255,0.08)',
              zIndex: 0
            }} />

            <Avatar sx={{ 
              width: 100, 
              height: 100, 
              mx: 'auto', 
              mb: 3, 
              bgcolor: 'rgba(255,255,255,0.2)',
              position: 'relative',
              zIndex: 1
            }}>
              <CheckCircleIcon sx={{ fontSize: 60, color: 'white' }} />
            </Avatar>

            <Typography 
              variant="h3" 
              fontWeight="bold" 
              gutterBottom
              sx={{ position: 'relative', zIndex: 1 }}
            >
              ¡Felicitaciones!
            </Typography>

            <Typography 
              variant="h6" 
              sx={{ opacity: 0.9, position: 'relative', zIndex: 1 }}
            >
              Has completado exitosamente tu test vocacional
            </Typography>
          </Box>
        </Paper>

        {/* Perfil Vocacional */}
        <Grid container spacing={4}>
          <Grid item xs={12} md={6}>
            <Paper 
              elevation={0} 
              sx={{ 
                borderRadius: '20px', 
                p: 4, 
                height: '100%',
                border: '2px solid #b9f6ca',
                background: 'linear-gradient(135deg, #ffffff 0%, #f9fff9 100%)'
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                <Avatar sx={{ 
                  width: 60, 
                  height: 60, 
                  mr: 2, 
                  bgcolor: '#4caf50' 
                }}>
                  <TrendingUpIcon sx={{ fontSize: 30 }} />
                </Avatar>
                <Box>
                  <Typography variant="h5" color="#2e7d32" fontWeight="600">
                    Tu Perfil Vocacional
                  </Typography>
                  <Typography variant="body2" color="#666">
                    Basado en tus respuestas
                  </Typography>
                </Box>
              </Box>

              <Divider sx={{ my: 3, borderColor: '#b9f6ca' }} />

              <Typography 
                variant="h4" 
                color="#2e7d32" 
                fontWeight="bold" 
                textAlign="center"
                sx={{ 
                  py: 3,
                  px: 2,
                  backgroundColor: '#e8f5e8',
                  borderRadius: '16px',
                  border: '1px solid #b9f6ca'
                }}
              >
                {carreras.length > 0 ? carreras[0].area?.nombre || "Área Identificada" : "Área Sugerida"}
              </Typography>

              <Typography 
                variant="body1" 
                textAlign="center" 
                color="#666"
                sx={{ mt: 2 }}
              >
                Esta área se alinea perfectamente con tus intereses y aptitudes
              </Typography>
            </Paper>
          </Grid>

          {/* Carreras Recomendadas */}
          <Grid item xs={12} md={6}>
            <Paper 
              elevation={0} 
              sx={{ 
                borderRadius: '20px', 
                p: 4, 
                height: '100%',
                border: '2px solid #b9f6ca',
                background: 'linear-gradient(135deg, #ffffff 0%, #f9fff9 100%)'
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                <Avatar sx={{ 
                  width: 60, 
                  height: 60, 
                  mr: 2, 
                  bgcolor: '#4caf50' 
                }}>
                  <SchoolIcon sx={{ fontSize: 30 }} />
                </Avatar>
                <Box>
                  <Typography variant="h5" color="#2e7d32" fontWeight="600">
                    Carreras Recomendadas
                  </Typography>
                  <Typography variant="body2" color="#666">
                    {carreras.length} opciones encontradas
                  </Typography>
                </Box>
              </Box>

              <Divider sx={{ my: 3, borderColor: '#b9f6ca' }} />

              <Box sx={{ 
                maxHeight: 250, 
                overflow: 'auto',
                '&::-webkit-scrollbar': {
                  width: 8,
                },
                '&::-webkit-scrollbar-track': {
                  background: '#f1f1f1',
                  borderRadius: 4,
                },
                '&::-webkit-scrollbar-thumb': {
                  background: '#4caf50',
                  borderRadius: 4,
                },
              }}>
                <Grid container spacing={2}>
                  {carreras.map((carrera, index) => (
                    <Grid item xs={12} key={carrera.id}>
                      <Card 
                        elevation={0}
                        sx={{ 
                          border: '1px solid #b9f6ca',
                          borderRadius: '12px',
                          transition: 'all 0.3s ease',
                          '&:hover': {
                            transform: 'translateY(-2px)',
                            boxShadow: '0 4px 20px rgba(76, 175, 80, 0.15)',
                            borderColor: '#4caf50'
                          }
                        }}
                      >
                        <CardContent sx={{ p: 2 }}>
                          <Box sx={{ display: 'flex', alignItems: 'center' }}>
                            <Box sx={{
                              width: 40,
                              height: 40,
                              borderRadius: '50%',
                              background: `linear-gradient(45deg, #4caf50, #2e7d32)`,
                              display: 'flex',
                              alignItems: 'center',
                              justifyContent: 'center',
                              mr: 2,
                              color: 'white',
                              fontSize: '16px',
                              fontWeight: 'bold'
                            }}>
                              {index + 1}
                            </Box>
                            <Typography 
                              variant="h6" 
                              color="#2e7d32" 
                              fontWeight="600"
                              sx={{ flex: 1 }}
                            >
                              {carrera.nombre}
                            </Typography>
                          </Box>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              </Box>
            </Paper>
          </Grid>
        </Grid>

        {/* Acciones */}
        <Box sx={{ textAlign: 'center', mt: 6 }}>
          <Button
            onClick={onRestart}
            variant="contained"
            size="large"
            startIcon={<RestartAltIcon />}
            sx={{
              backgroundColor: '#4caf50',
              color: 'white',
              px: 4,
              py: 2,
              borderRadius: '16px',
              fontSize: '18px',
              fontWeight: '600',
              textTransform: 'none',
              boxShadow: '0 8px 24px rgba(76, 175, 80, 0.3)',
              '&:hover': {
                backgroundColor: '#2e7d32',
                transform: 'translateY(-2px)',
                boxShadow: '0 12px 32px rgba(76, 175, 80, 0.4)',
              },
              transition: 'all 0.3s ease'
            }}
          >
            Realizar Test Nuevamente
          </Button>

          <Typography 
            variant="body2" 
            color="#666" 
            sx={{ mt: 2 }}
          >
            ¿Quieres explorar otras opciones? Puedes repetir el test cuando gustes
          </Typography>
        </Box>
      </Box>
    </Box>
  </Fade>
);

export default function TestFlow() {
  const [proceso, setProceso] = useState(null);
  const [preguntas, setPreguntas] = useState([]);
  const [current, setCurrent] = useState(0);
  const [respuestas, setRespuestas] = useState([]);
  const [finished, setFinished] = useState(false);
  const [areaSugerida, setAreaSugerida] = useState(null);
  const [carreras, setCarreras] = useState([]);
  const [loading, setLoading] = useState(true);

  const usuario_id = localStorage.getItem("usuario_id");

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
        const procesos = await getProcesses();
        const procesoActivo = procesos[0];
        setProceso(procesoActivo);
        const preguntasBD = await getQuestions(procesoActivo.id);
        setPreguntas(preguntasBD);
      } catch (e) {
        console.error("Error cargando datos:", e);
      }
      setLoading(false);
    }
    fetchData();
  }, []);

  // Calcular sugerencia cuando termine
  useEffect(() => {
    if (finished && respuestas.length === preguntas.length) {
      const areaCount = {};

      respuestas.forEach(r => {
        const pregunta = preguntas.find(p => p.id === r.pregunta_id);
        if (!pregunta) return;
        const opcion = pregunta.opciones.find(o => o.id === r.opcion_id);
        console.log("DEBUG opcion:", opcion);
        // Contar solo "Me interesa" (interes === 1 o true)
        if (opcion && opcion.texto.toLowerCase().includes("me interesa")) {
          areaCount[opcion.area_id] = (areaCount[opcion.area_id] || 0) + 1;
          console.log("Se sumó interés en área:", opcion.area_id, "Total:", areaCount[opcion.area_id]);
        }
      });

      const area_id = Object.entries(areaCount).sort((a, b) => b[1] - a[1])[0]?.[0] ?? null;
      setAreaSugerida(area_id);

      saveResponses(usuario_id, proceso.id, respuestas);
      if (area_id) {
        saveSuggestion(usuario_id, area_id);
        getCareers(area_id).then(setCarreras);
      }
    }
  }, [finished, respuestas, preguntas, usuario_id, proceso]);

  const handleRestart = () => {
    setCurrent(0);
    setRespuestas([]);
    setFinished(false);
    setAreaSugerida(null);
    setCarreras([]);
  };

  if (loading) {
    return (
      <Box sx={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center'
      }}>
        <Paper elevation={0} sx={{ p: 4, borderRadius: '16px', textAlign: 'center' }}>
          <Typography variant="h5" color="#2e7d32" fontWeight="600">
            Cargando test...
          </Typography>
        </Paper>
      </Box>
    );
  }

  // Mostrar resultados si el test ha terminado
  if (finished && carreras.length > 0) {
    return (
      <ResultsView 
        areaSugerida={areaSugerida}
        carreras={carreras}
        onRestart={handleRestart}
      />
    );
  }

  const preguntaActual = preguntas[current];
  if (!preguntaActual) {
    return (
      <Box sx={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center'
      }}>
        <Paper elevation={0} sx={{ p: 4, borderRadius: '16px', textAlign: 'center' }}>
          <Typography variant="h5" color="#2e7d32" fontWeight="600">
            Cargando test...
          </Typography>
        </Paper>
      </Box>
    );
  }

  const handleRespuesta = (opcion_id) => {
    setRespuestas(prev => [...prev, { pregunta_id: preguntaActual.id, opcion_id }]);
    if (current < preguntas.length - 1) {
      setCurrent(current + 1);
    } else {
      setFinished(true);
    }
  };

  const progress = ((current + 1) / preguntas.length) * 100;

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      width: '100vw', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'center',
      py: 4,
      px: 2
    }}>
      <Fade in={true} timeout={600} key={current}>
        <Box sx={{ width: '100%', maxWidth: 800 }}>
          {/* Barra de progreso mejorada */}
          <Box sx={{ mb: 4 }}>
            <ProgressBar value={progress} />
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Typography variant="body1" color="#2e7d32" fontWeight="600">
                Pregunta {current + 1} de {preguntas.length}
              </Typography>
              <Chip 
                label={`${Math.round(progress)}% Completado`} 
                color="success" 
                variant="outlined" 
                sx={{ fontWeight: 600 }}
              />
            </Box>
          </Box>

          <Paper 
            elevation={0} 
            sx={{ 
              borderRadius: '32px', 
              overflow: 'hidden',
              border: '3px solid #b9f6ca',
              boxShadow: '0 16px 48px rgba(76, 175, 80, 0.15)'
            }}
          >
            {/* Pregunta mejorada */}
            <Box sx={{ 
              borderBottom: '3px solid #b9f6ca', 
              p: 4, 
              textAlign: 'center',
              background: 'linear-gradient(135deg, #ffffff 0%, #f9fff9 100%)'
            }}>
              <Typography 
                variant="h4" 
                color="#2e7d32" 
                fontWeight="bold"
                sx={{ lineHeight: 1.3 }}
              >
                {preguntaActual.texto}
              </Typography>
            </Box>

            {/* Opciones mejoradas */}
            <Box sx={{ 
              display: 'flex', 
              flexDirection: { xs: 'column', sm: 'row' }, 
              height: { xs: 'auto', sm: 250 } 
            }}>
              {preguntaActual.opciones.map((op, idx) => (
                <Box
                  key={op.id}
                  sx={{
                    flex: 1,
                    borderRight: idx === 0 ? { sm: '3px solid #b9f6ca', xs: 'none' } : 'none',
                    borderBottom: idx === 0 ? { xs: '3px solid #b9f6ca', sm: 'none' } : 'none',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    position: 'relative',
                    overflow: 'hidden'
                  }}
                >
                  <Button
                    onClick={() => handleRespuesta(op.id)}
                    variant="contained"
                    fullWidth
                    sx={{
                      height: { xs: 120, sm: 250 },
                      borderRadius: 0,
                      background: 'linear-gradient(135deg, #90EE90 0%, #7dd87d 100%)',
                      color: "#2e7d32",
                      fontSize: { xs: 28, sm: 32 },
                      fontWeight: 600,
                      textTransform: 'none',
                      position: 'relative',
                      overflow: 'hidden',
                      transition: 'all 0.3s ease',
                      "&:hover": { 
                        background: 'linear-gradient(135deg, #77d677 0%, #66cc66 100%)',
                        transform: 'scale(1.02)',
                        boxShadow: '0 8px 24px rgba(76, 175, 80, 0.3)'
                      },
                      "&:before": {
                        content: '""',
                        position: 'absolute',
                        top: 0,
                        left: '-100%',
                        width: '100%',
                        height: '100%',
                        background: 'linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent)',
                        transition: 'left 0.5s ease',
                      },
                      "&:hover:before": {
                        left: '100%'
                      }
                    }}
                  >
                    {op.texto}
                  </Button>
                </Box>
              ))}
            </Box>
          </Paper>
        </Box>
      </Fade>
    </Box>
  );
}
