  },
  {
    question: "Si participas en un equipo, ¬øqu√© rol prefieres?",
    options: [
      { text: "Dise√±ar el plan t√©cnico", area: "Ingenier√≠a", icon: "üìä" },
      { text: "Aportar ideas creativas", area: "Artes", icon: "üí≠" },
      { text: "Ser portavoz y l√≠der del grupo", area: "Ciencias Sociales", icon: "üó£Ô∏è" },
      { text: "Cuidar el bienestar del equipo", area: "Salud", icon: "üëê" }
    ]
  },
  {
    question: "¬øQu√© logro te har√≠a sentir m√°s orgulloso?",
    options: [
      { text: "Inventar una tecnolog√≠a √∫til", area: "Ingenier√≠a", icon: "‚öôÔ∏è" },
      { text: "Exponer una obra en una galer√≠a", area: "Artes", icon: "üñºÔ∏è" },
      { text: "Influir en una pol√≠tica p√∫blica", area: "Ciencias Sociales", icon: "üèõÔ∏è" },
      { text: "Salvar o mejorar vidas", area: "Salud", icon: "üå°Ô∏è" }
