  }
];

const areaDescriptions = {
  "Ingenier√≠a": {
    description: "Tienes una mente anal√≠tica y te apasiona resolver problemas complejos mediante la tecnolog√≠a y las matem√°ticas.",
    careers: ["Ingenier√≠a de Inform√°tica", "Ingenier√≠a Civil", "Ingenier√≠a Industrial", "Ingenier√≠a Electr√≥nica"],
    icon: <TrendingUp sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Artes": {
    description: "Posees una sensibilidad especial para la creatividad y la expresi√≥n art√≠stica en sus diversas formas.",
    careers: ["Dise√±o Gr√°fico", "Artes Visuales", "M√∫sica", "Teatro", "Arquitectura"],
    icon: <Psychology sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Ciencias Sociales": {
    description: "Te interesa comprender y mejorar las din√°micas sociales, pol√≠ticas y econ√≥micas de la sociedad.",
    careers: ["Psicolog√≠a", "Sociolog√≠a", "Ciencias Pol√≠ticas", "Trabajo Social", "Derecho"],
    icon: <School sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Salud": {
    description: "Tienes vocaci√≥n de servicio y te motiva contribuir al bienestar f√≠sico y mental de las personas.",
    careers: ["Medicina", "Enfermer√≠a", "Fisioterapia", "Nutrici√≥n", "Odontolog√≠a"],
    icon: <CheckCircle sx={{ fontSize: 48, color: '#2e7d32' }} />
  }
};

export default function TestFlow() {
  const [current, setCurrent] = useState(0);
  const [scores, setScores] = useState({
    Ingenier√≠a: 0,
    Artes: 0,
    "Ciencias Sociales": 0,
    Salud: 0
  });
  const [finished, setFinished] = useState(false);
  const [result, setResult] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);

  const handleAnswer = (area, optionIndex) => {
    setSelectedOption(optionIndex);

    setTimeout(() => {
      setScores((prev) => ({
        ...prev,
        [area]: prev[area] + 1
      }));

      if (current < questions.length - 1) {
        setCurrent(current + 1);
        setSelectedOption(null);
      } else {
        // calcular el resultado final
        const newScores = {
          ...scores,
          [area]: scores[area] + 1
        };

        const maxArea = Object.entries(newScores).reduce(
          (a, b) => (a[1] > b[1] ? a : b)
        );
        setResult(maxArea[0]);
        setFinished(true);
      }
    }, 500);
  };

  if (finished) {
    const resultData = areaDescriptions[result];

    return (
      <Box
        sx={{
          minHeight: '100vh',
        width: '100%',
        background: 'linear-gradient(135deg, #e8f5e8 0%, #f1f8e9 100%)',
        position: 'flex',
        top: 0,
        left: 0,
        zIndex: -1
        }}
      >
        <Box sx={{ minHeight: '100vh', display: 'flex', alignItems: 'flex-start', justifyContent: 'center', pt: 4 }}>
        <Container maxWidth="md">
          <Fade in={true} timeout={800}>
            <Paper
              elevation={0}
              sx={{
                p: 6,
                borderRadius: '32px',
                textAlign: "center",
                background: 'linear-gradient(135deg, #ffffff 0%, #f8fffe 100%)',
                boxShadow: '0 20px 60px rgba(46, 125, 50, 0.1)',
                border: '1px solid rgba(76, 175, 80, 0.1)'
              }}
            >
              {/* Icono y celebraci√≥n */}
              <Box sx={{ mb: 4 }}>
                <Box
                  sx={{
                    width: 120,
                    height: 120,
                    borderRadius: '50%',
                    background: 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    margin: '0 auto',
                    mb: 3,
                    boxShadow: '0 10px 30px rgba(76, 175, 80, 0.3)'
                  }}
                >
                  {resultData.icon}
                </Box>
                <Typography 
                  variant="h3" 
                  sx={{ 
                    fontWeight: 'bold',
                    background: 'linear-gradient(135deg, #2e7d32 0%, #4caf50 100%)',
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    mb: 2
                  }}
                >
                  üéâ ¬°Felicitaciones!
                </Typography>
              </Box>

              <Typography variant="h4" color="#1b5e20" fontWeight="bold" gutterBottom>
                Tu perfil vocacional es:
              </Typography>

              <Typography 
                variant="h2" 
                sx={{ 
                  fontWeight: "bold", 
                  background: 'linear-gradient(135deg, #2e7d32 0%, #4caf50 100%)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  mb: 4
                }}
              >
                {result}
