import { useState } from "react";
import { Box, Card, Typography, Button, Container, LinearProgress, Paper, Fade } from "@mui/material";
import { CheckCircle, TrendingUp, Psychology, School } from "@mui/icons-material";

// Componente de ProgressBar mejorado
const ProgressBar = ({ value }) => (
  <Box sx={{ mb: 4 }}>
    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
      <Typography variant="body2" color="#2e7d32" fontWeight="600">
        Progreso del Test
      </Typography>
      <Typography variant="body2" color="#2e7d32" fontWeight="600">
        {Math.round(value)}%
      </Typography>
    </Box>
    <LinearProgress
      variant="determinate"
      value={value}
      sx={{
        height: 12,
        borderRadius: 6,
        backgroundColor: '#e8f5e8',
        '& .MuiLinearProgress-bar': {
          background: 'linear-gradient(90deg, #4caf50 0%, #2e7d32 100%)',
          borderRadius: 6,
        },
      }}
    />
  </Box>
);

// Preguntas con opciones asociadas a áreas vocacionales
const questions = [
  {
    question: "¿Qué actividad disfrutas más?",
    options: [
      { text: "Resolver problemas matemáticos", area: "Ingeniería", icon: "🔧" },
      { text: "Dibujar o diseñar cosas creativas", area: "Artes", icon: "🎨" },
      { text: "Debatir temas sociales y políticos", area: "Ciencias Sociales", icon: "💬" },
      { text: "Investigar sobre el cuerpo humano", area: "Salud", icon: "🏥" }
    ]
  },
  {
    question: "Si tuvieras que elegir un proyecto, ¿cuál preferirías?",
    options: [
      { text: "Construir un robot", area: "Ingeniería", icon: "🤖" },
      { text: "Montar una obra de teatro", area: "Artes", icon: "🎭" },
      { text: "Hacer una encuesta sobre la comunidad", area: "Ciencias Sociales", icon: "📊" },
      { text: "Diseñar una campaña de prevención médica", area: "Salud", icon: "💊" }
    ]
  },
  {
    question: "¿Qué materia disfrutas más en la escuela?",
    options: [
      { text: "Física o Matemáticas", area: "Ingeniería", icon: "📐" },
      { text: "Arte o Música", area: "Artes", icon: "🎵" },
      { text: "Historia o Filosofía", area: "Ciencias Sociales", icon: "📚" },
      { text: "Biología o Química", area: "Salud", icon: "🧬" }
    ]
  },
  {
    question: "¿Cómo prefieres trabajar?",
    options: [
      { text: "Analizando datos y fórmulas", area: "Ingeniería", icon: "📈" },
      { text: "Expresando ideas creativas", area: "Artes", icon: "✨" },
      { text: "Colaborando con personas en discusiones", area: "Ciencias Sociales", icon: "👥" },
      { text: "Ayudando a otros en su bienestar", area: "Salud", icon: "🤝" }
    ]
  },
  {
    question: "¿Qué te motiva más en tu futuro profesional?",
    options: [
      { text: "Crear soluciones tecnológicas innovadoras", area: "Ingeniería", icon: "💡" },
      { text: "Inspirar y emocionar a las personas", area: "Artes", icon: "🌟" },
      { text: "Generar cambios positivos en la sociedad", area: "Ciencias Sociales", icon: "🌍" },
      { text: "Mejorar la calidad de vida de otros", area: "Salud", icon: "❤️" }
    ]
  },
  {
    question: "¿Qué tipo de retos disfrutas más?",
    options: [
      { text: "Resolver acertijos lógicos o técnicos", area: "Ingeniería", icon: "🧩" },
      { text: "Crear algo nuevo desde cero", area: "Artes", icon: "🎬" },
      { text: "Mediar en un conflicto y dar soluciones", area: "Ciencias Sociales", icon: "🤝" },
      { text: "Encontrar la causa de un síntoma o problema de salud", area: "Salud", icon: "🩺" }
    ]
  },
  {
    question: "¿Qué herramienta preferirías usar en un proyecto?",
    options: [
      { text: "Un software de programación", area: "Ingeniería", icon: "💻" },
      { text: "Un set de pinceles o instrumentos", area: "Artes", icon: "🎹" },
      { text: "Un micrófono para entrevistas", area: "Ciencias Sociales", icon: "🎤" },
      { text: "Un kit de primeros auxilios", area: "Salud", icon: "🧰" }
    ]
  },
  {
    question: "¿Qué tipo de lectura disfrutas más?",
    options: [
      { text: "Revistas científicas y tecnología", area: "Ingeniería", icon: "🔬" },
      { text: "Novelas, cómics o poesía", area: "Artes", icon: "📖" },
      { text: "Artículos de actualidad social", area: "Ciencias Sociales", icon: "📰" },
      { text: "Manuales de biología o salud", area: "Salud", icon: "📘" }
    ]
  },
  {
    question: "Si participas en un equipo, ¿qué rol prefieres?",
    options: [
      { text: "Diseñar el plan técnico", area: "Ingeniería", icon: "📊" },
      { text: "Aportar ideas creativas", area: "Artes", icon: "💭" },
      { text: "Ser portavoz y líder del grupo", area: "Ciencias Sociales", icon: "🗣️" },
      { text: "Cuidar el bienestar del equipo", area: "Salud", icon: "👐" }
    ]
  },
  {
    question: "¿Qué logro te haría sentir más orgulloso?",
    options: [
      { text: "Inventar una tecnología útil", area: "Ingeniería", icon: "⚙️" },
      { text: "Exponer una obra en una galería", area: "Artes", icon: "🖼️" },
      { text: "Influir en una política pública", area: "Ciencias Sociales", icon: "🏛️" },
      { text: "Salvar o mejorar vidas", area: "Salud", icon: "🌡️" }
    ]
  }
];

const areaDescriptions = {
  "Ingeniería": {
    description: "Tienes una mente analítica y te apasiona resolver problemas complejos mediante la tecnología y las matemáticas.",
    careers: ["Ingeniería de Informática", "Ingeniería Civil", "Ingeniería Industrial", "Ingeniería Electrónica"],
    icon: <TrendingUp sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Artes": {
    description: "Posees una sensibilidad especial para la creatividad y la expresión artística en sus diversas formas.",
    careers: ["Diseño Gráfico", "Artes Visuales", "Música", "Teatro", "Arquitectura"],
    icon: <Psychology sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Ciencias Sociales": {
    description: "Te interesa comprender y mejorar las dinámicas sociales, políticas y económicas de la sociedad.",
    careers: ["Psicología", "Sociología", "Ciencias Políticas", "Trabajo Social", "Derecho"],
    icon: <School sx={{ fontSize: 48, color: '#2e7d32' }} />
  },
  "Salud": {
    description: "Tienes vocación de servicio y te motiva contribuir al bienestar físico y mental de las personas.",
    careers: ["Medicina", "Enfermería", "Fisioterapia", "Nutrición", "Odontología"],
    icon: <CheckCircle sx={{ fontSize: 48, color: '#2e7d32' }} />
  }
};

export default function TestFlow() {
  const [current, setCurrent] = useState(0);
  const [scores, setScores] = useState({
    Ingeniería: 0,
    Artes: 0,
    "Ciencias Sociales": 0,
    Salud: 0
  });
  const [finished, setFinished] = useState(false);
  const [result, setResult] = useState(null);
  const [selectedOption, setSelectedOption] = useState(null);

  const handleAnswer = (area, optionIndex) => {
    setSelectedOption(optionIndex);

    setTimeout(() => {
      setScores((prev) => ({
        ...prev,
        [area]: prev[area] + 1
      }));

      if (current < questions.length - 1) {
        setCurrent(current + 1);
        setSelectedOption(null);
      } else {
        // calcular el resultado final
        const newScores = {
          ...scores,
          [area]: scores[area] + 1
        };

        const maxArea = Object.entries(newScores).reduce(
          (a, b) => (a[1] > b[1] ? a : b)
        );
        setResult(maxArea[0]);
        setFinished(true);
      }
    }, 500);
  };

  if (finished) {
    const resultData = areaDescriptions[result];

    return (
      <Box
        sx={{
          minHeight: '100vh',
        width: '100%',
        background: 'linear-gradient(135deg, #e8f5e8 0%, #f1f8e9 100%)',
        position: 'flex',
        top: 0,
        left: 0,
        zIndex: -1
        }}
      >
        <Box sx={{ minHeight: '100vh', display: 'flex', alignItems: 'flex-start', justifyContent: 'center', pt: 4 }}>
        <Container maxWidth="md">
          <Fade in={true} timeout={800}>
            <Paper
              elevation={0}
              sx={{
                p: 6,
                borderRadius: '32px',
                textAlign: "center",
                background: 'linear-gradient(135deg, #ffffff 0%, #f8fffe 100%)',
                boxShadow: '0 20px 60px rgba(46, 125, 50, 0.1)',
                border: '1px solid rgba(76, 175, 80, 0.1)'
              }}
            >
              {/* Icono y celebración */}
              <Box sx={{ mb: 4 }}>
                <Box
                  sx={{
                    width: 120,
                    height: 120,
                    borderRadius: '50%',
                    background: 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    margin: '0 auto',
                    mb: 3,
                    boxShadow: '0 10px 30px rgba(76, 175, 80, 0.3)'
                  }}
                >
                  {resultData.icon}
                </Box>
                <Typography 
                  variant="h3" 
                  sx={{ 
                    fontWeight: 'bold',
                    background: 'linear-gradient(135deg, #2e7d32 0%, #4caf50 100%)',
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    mb: 2
                  }}
                >
                  🎉 ¡Felicitaciones!
                </Typography>
              </Box>

              <Typography variant="h4" color="#1b5e20" fontWeight="bold" gutterBottom>
                Tu perfil vocacional es:
              </Typography>

              <Typography 
                variant="h2" 
                sx={{ 
                  fontWeight: "bold", 
                  background: 'linear-gradient(135deg, #2e7d32 0%, #4caf50 100%)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  mb: 4
                }}
              >
                {result}
              </Typography>

              <Paper
                sx={{
                  p: 4,
                  mb: 4,
                  backgroundColor: '#f1f8e9',
                  border: '2px solid #c8e6c9',
                  borderRadius: '20px'
                }}
              >
                <Typography variant="h6" color="#2e7d32" sx={{ mb: 2 }}>
                  Descripción de tu perfil:
                </Typography>
                <Typography variant="body1" color="#1b5e20" sx={{ lineHeight: 1.8 }}>
                  {resultData.description}
                </Typography>
              </Paper>

              <Paper
                sx={{
                  p: 4,
                  backgroundColor: '#e8f5e8',
                  border: '2px solid #a5d6a7',
                  borderRadius: '20px',
                  mb: 4
                }}
              >
                <Typography variant="h6" color="#2e7d32" sx={{ mb: 3 }}>
                  Carreras recomendadas para ti:
                </Typography>
                <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, justifyContent: 'center' }}>
                  {resultData.careers.map((career, index) => (
                    <Paper
                      key={index}
                      sx={{
                        px: 3,
                        py: 1.5,
                        backgroundColor: 'white',
                        border: '1px solid #4caf50',
                        borderRadius: '25px',
                        boxShadow: '0 4px 12px rgba(76, 175, 80, 0.2)'
                      }}
                    >
                      <Typography variant="body2" color="#2e7d32" fontWeight="600">
                        {career}
                      </Typography>
                    </Paper>
                  ))}
                </Box>
              </Paper>

              <Button
                variant="contained"
                size="large"
                onClick={() => window.location.reload()}
                sx={{
                  background: 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)',
                  color: 'white',
                  fontWeight: 'bold',
                  py: 2,
                  px: 4,
                  fontSize: '1.1rem',
                  borderRadius: '16px',
                  textTransform: 'none',
                  boxShadow: '0 8px 24px rgba(76, 175, 80, 0.4)',
                  '&:hover': {
                    background: 'linear-gradient(135deg, #388e3c 0%, #1b5e20 100%)',
                    boxShadow: '0 12px 32px rgba(76, 175, 80, 0.6)',
                    transform: 'translateY(-2px)',
                  },
                  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)'
                }}
              >
                Realizar Test Nuevamente
              </Button>
            </Paper>
          </Fade>
        </Container>
        </Box>
      </Box>
    );
  }

  const data = questions[current];

  return (
    <Box
      sx={{
        minHeight: '100vh',
        width: '100vw',
        background: 'linear-gradient(135deg, #e8f5e8 0%, #f1f8e9 100%)',
        position: 'fixed',
        top: 0,
        left: 0,
        zIndex: -1
      }}
    >
      <Box sx={{ minHeight: '100vh', display: 'flex', alignItems: 'flex-start', justifyContent: 'center', pt: 4 }}>
        <Container maxWidth="md">
          <Fade in={true} timeout={600} key={current}>
            <Box>
            <ProgressBar value={((current + 1) / questions.length) * 100} />

            <Paper
              elevation={0}
              sx={{
                p: 5,
                borderRadius: '32px',
                background: 'linear-gradient(135deg, #ffffff 0%, #f8fffe 100%)',
                boxShadow: '0 20px 60px rgba(46, 125, 50, 0.1)',
                border: '1px solid rgba(76, 175, 80, 0.1)'
              }}
            >
              <Box sx={{ textAlign: 'center', mb: 4 }}>
                <Typography 
                  variant="body1" 
                  color="#4caf50" 
                  fontWeight="600" 
                  sx={{ mb: 1 }}
                >
                  Pregunta {current + 1} de {questions.length}
                </Typography>
                <Typography 
                  variant="h4" 
                  color="#1b5e20"
                  fontWeight="bold"
                  sx={{ lineHeight: 1.4 }}
                >
                  {data.question}
                </Typography>
              </Box>

              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                {data.options.map((option, i) => (
                  <Button
                    key={i}
                    variant={selectedOption === i ? "contained" : "outlined"}
                    fullWidth
                    size="large"
                    onClick={() => handleAnswer(option.area, i)}
                    disabled={selectedOption !== null}
                    sx={{
                      py: 3,
                      px: 4,
                      fontSize: '1.1rem',
                      fontWeight: '600',
                      borderRadius: '20px',
                      textAlign: 'left',
                      justifyContent: 'flex-start',
                      textTransform: 'none',
                      border: selectedOption === i ? 'none' : '2px solid #c8e6c9',
                      backgroundColor: selectedOption === i 
                        ? 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)' 
                        : 'white',
                      color: selectedOption === i ? 'white' : '#2e7d32',
                      boxShadow: selectedOption === i 
                        ? '0 8px 24px rgba(76, 175, 80, 0.4)'
                        : '0 4px 12px rgba(76, 175, 80, 0.1)',
                      '&:hover': {
                        backgroundColor: selectedOption === i 
                          ? 'linear-gradient(135deg, #4caf50 0%, #2e7d32 100%)'
                          : '#f1f8e9',
                        borderColor: '#4caf50',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(76, 175, 80, 0.2)',
                      },
                      '&:disabled': {
                        opacity: selectedOption === i ? 1 : 0.6,
                      },
                      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                      display: 'flex',
                      alignItems: 'center',
                      gap: 2
                    }}
                  >
                    <Typography sx={{ fontSize: '1.5rem', mr: 1 }}>
                      {option.icon}
                    </Typography>
                    {option.text}
                  </Button>
                ))}
              </Box>
            </Paper>
          </Box>
        </Fade>
      </Container>
      </Box>
    </Box>
  );
}
