import { useState } from "react";
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  TextField,
  MenuItem,
  Box
} from "@mui/material";
import { useNavigate } from "react-router-dom";

const niveles = [
  "Secundaria incompleta",
  "Secundaria completa",
  "Preuniversitario",
  "Otro"
];

export default function ModalStart({ open, onClose }) {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    nombres: "",
    apellidos: "",
    dni: "",
    nivel: "",
    email: "",
    celular: ""
  });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setError("");
  };

  const handleStart = () => {
    // Validación simple
    if (
      !form.nombres ||
      !form.apellidos ||
      !form.dni ||
      !form.nivel ||
      !form.email ||
      !form.celular
    ) {
      setError("Completa todos los campos obligatorios.");
      return;
    }
    // Aquí podrías guardar los datos si lo necesitas
    navigate("/test");
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle sx={{ backgroundColor: "#006400", color: "white" }}>
        Test Vocacional URP
      </DialogTitle>
      <DialogContent>
        <Typography sx={{ mt: 1, mb: 2 }}>
          Completa tus datos para empezar el test rápido y personalizado.
        </Typography>
        <Box component="form" sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
          <TextField
            label="Nombres"
            name="nombres"
            value={form.nombres}
            onChange={handleChange}
            required
          />
          <TextField
            label="Apellidos"
            name="apellidos"
            value={form.apellidos}
            onChange={handleChange}
            required
          />
          <TextField
            label="DNI"
            name="dni"
            value={form.dni}
            onChange={handleChange}
            required
            inputProps={{ maxLength: 8 }}
          />
          <TextField
            select
            label="Nivel de estudios"
            name="nivel"
            value={form.nivel}
            onChange={handleChange}
            required
          >
            {niveles.map((nivel) => (
              <MenuItem key={nivel} value={nivel}>
                {nivel}
              </MenuItem>
            ))}
          </TextField>
          <TextField
            label="Email"
            name="email"
            value={form.email}
            onChange={handleChange}
            required
            type="email"
          />
          <TextField
            label="Celular"
            name="celular"
            value={form.celular}
            onChange={handleChange}
            required
            inputProps={{ maxLength: 9 }}
          />
          {error && (
            <Typography color="error" variant="body2">
              {error}
            </Typography>
          )}
        </Box>
      </DialogContent>
      <DialogActions sx={{ p: 3 }}>
        <Button
          variant="contained"
          color="success"
          onClick={handleStart}
          fullWidth
          sx={{ backgroundColor: "#90EE90", color: "#006400", fontWeight: "bold" }}
        >
          Iniciar Test
        </Button>
      </DialogActions>
    </Dialog>
  );
}
